function start(){one(),two(),three(),makeVisible()}function one(){var e=document.getElementById("one");e.classList.add("blue")}function two(){var e=document.getElementById("two");e.classList.add("green")}function three(){var e=document.getElementById("three");e.classList.add("orange")}function makeVisible(){var e=document.getElementsByClassName("invisible")[0];e.classList.add("visible")}document.addEventListener("DOMContentLoaded",start);